import '../styles/App.css';
import React from 'react';
import '../styles/Footer.css';
import { useNavigate } from "react-router-dom";
import { useRef } from 'react';
import Swal from "sweetalert2"

export const Historial = () => {
    const link2 = useNavigate();
    const local = JSON.parse(localStorage.getItem("datos"));

    const limpiar=(local)=>{
        localStorage.removeItem("datos")
        Swal.fire({
            position: 'center',
            icon: 'success',
            title: 'Historial borrado',
            showConfirmButton: false,
            timer: 1500
        }).then(()=>location.reload());

    const look = useRef();
    if(local === null){
        return( 
            <>
                <button onClick={() => {link2("/");  document.querySelector(".card").style="display:block"}} className="historial-link">Volver</button>
                <p>Historial vacio</p>
            </>) 
    }else{
        return (
            <>
                <div className="table-responsive">
                    <table className="table">
                        <thead>
                            <tr>
                                <th scope="col">Fecha de Cotización</th>
                                <th scope="col">Vivienda</th>
                                <th scope="col">Ubicación</th>
                                <th scope="col">Mts2.</th>
                                <th scope="col">Seguro Mensual</th>
                            </tr>
                        </thead>
                        <tbody className="table-group-divider">
                        {local.map(ver => {
                                return (
                                    <tr key={ver.id}>
                                        <td>{ver.fecha}</td>
                                        <td>{ver.propiedad}</td>
                                        <td>{ver.ubicacion}</td>
                                        <td>{ver.metrosCuadrados}</td>
                                        <td>{'$' + ver.poliza}</td>
                                    </tr>
                                );
                        })}   
                        </tbody>
                    </table>
                    <button onClick={() => {link2("/"); document.querySelector(".card").style="display:block"}} className="historial-link">Volver</button>
                    <button onClick={limpiar} type="button" className="historial-link">Borrar datos</button>
                </div>
        </>
    )};
                    }}
